        const newSearch = e.target.value;
        this.setState({ searchField: newSewarch })
    }

    render() {
        return (
            <div>
                <h1>Home</h1>
                <form>
                <input type="text"></input>
                <button>Search</button>
                <p>search: {this.state.searchField}</p>
